import React, { Component } from "react";
import "./Badge.css";

class Badge extends Component {
  render() {
    return (
      <div
        className="badge"
        style={{ fontSize: this.props.fsize ? this.props.fsize : "16px" }}
      >
        {this.props.children}
      </div>
    );
  }
}
export default Badge;
